mapper.select:
select * from (select rownum RNUM, zxcvb.* from( select developer_no ,developer_name ,jumin_no ,school_code ,religion_name as "RELIGION_NAME" ,graduate_day ,case substr(jumin_no,7,1) when '1' then '남' when '2' then '여' end as "sex"
from developer d ,code_religion r where d.religion_code = r.religion_code and 1=1
and developer_name like '%${keyword}%'
substr(jumin_no,7,1)=#{tmp}
school_code=#{tmp}
and to_number(graduate_day)>=to_number(#{first_day})
and to_number(#{second_day})>=to_number(graduate_day)
and d.religion_code = #{religion_name}
developer_no in (select developer_no from developer_skill where skill_code=${tmp})
)zxcvb )where RNUM >= #{begin_rowNo} and RNUM <= #{end_rowNo}

select count(*)
from developer d ,code_religion r where d.religion_code = r.religion_code and 1=1
and developer_name like '%${keyword}%'
substr(jumin_no,7,1)=#{tmp}
school_code=#{tmp}
and to_number(graduate_day)>=to_number(#{first_day})
and to_number(#{second_day})>=to_number(graduate_day)
and d.religion_code = #{religion_name}
developer_no in (select developer_no from developer_skill where skill_code=${tmp})
